mapper.insert:
INSERT INTO empo_community 
(community_ID, user_ID, community_choice, community_title, community_content, community_date, community_comment) 
SELECT LENGTH(#{community_ID})+1, #{user_ID}, #{community_choice}, #{community_title}, #{community_content}, #{community_date}, #{community_comment} 
FROM empo_community ORDER BY community_ID DESC LIMIT 1;
